<script>
import { indexlist } from "@/api/apis";
import echarts from "echarts";
export default {
  data() {
    return {
      form: {
        xData: [1, 2, 3, 4, 5, 6, 7], //x轴数据
        orderData: "", //订单数据
        amountData: "", //金额数据
        totalOrder: "", //总订单
        totalAmount: "", //总销售额
        todayOrder: "", //今日订单
        totayAmount: "", //今日销售额
      },
    };
  },
  //钩子函数
  mounted() {
    let mcharts = echarts.init(document.querySelector("#myChart"));

    indexlist().then((res) => {
      let option = {
        title: {
          text: "数据统计",
        },
        tooltip: {
          trigger: "axis",
        },
        legend: {
          data: ["金额数据", "订单数据"], //顶部描述数组
        },
        grid: {
          left: "3%",
          right: "4%",
          bottom: "3%",
          containLabel: true,
        },
        toolbox: {
          feature: {
            saveAsImage: {},
          },
        },
        xAxis: {
          type: "category",
          boundaryGap: false,
          data: res.data.xData, //横轴
        },
        yAxis: {
          type: "value",
        },
        series: [
          {
            name: "金额数据",
            type: "line",
            stack: "总量",
            data: res.data.amountData,
          },
          {
            name: "订单数据",
            type: "line",
            stack: "总量",
            data: res.data.orderData,
          },
        ],
      };
      mcharts.setOption(option);
    });
    // 3. 使用echarts实例对象， 关联数据配置对象， 创建表格
  },

  methods: {
    loadnum() {
      indexlist().then((res) => {
        this.form = res.data;
      });
    },
  },
  created() {
    this.loadnum();
  },
};
</script>

